import * as React from "react"
import { cva, type VariantProps } from "class-variance-authority"

import { cn } from "@/lib/utils"

import "./badge.scss"

const badgeVariants = cva("badge", {
  variants: {
    variant: {
      secondary: "badge-secondary",
      destructive: "badge-destructive",
      outline: "badge-outline",
    },
  },
})

export interface BadgeProps
  extends React.HTMLAttributes<HTMLDivElement>,
    VariantProps<typeof badgeVariants> {}

function Badge({ className, variant, ...props }: BadgeProps) {
  return (
    <div className={cn(badgeVariants({ variant }), className)} {...props} />
  )
}

export { Badge, badgeVariants }

.badge {
  display: inline-flex;
  align-items: center;
  border-radius: var(--border-radius-rounded);
  border: var(--border-size-s) solid transparent;
  padding: var(--space-xs) var(--space-s);
  font-size: var(--font-size-xs);
  font-weight: var(--font-semi-bold);
  transition-property: color, background-color, border-color,
    text-decoration-color, fill, stroke;
  transition-timing-function: cubic-bezier(0.4, 0, 0.2, 1);
  transition-duration: 0.15s;
  background-color: hsl(var(--primary));
  color: hsl(var(--primary-foreground));

  &:hover {
    background-color: hsl(var(--primary) / 0.8);
  }

  &:focus {
    outline: 2px solid transparent;
    outline-offset: 2px;
    box-shadow: 0 0 #0000;
  }

  &-secondary {
    border-color: transparent;
    background-color: hsl(var(--secondary));
    color: hsl(var(--secondary-foreground));

    &:hover {
      background-color: hsl(var(--secondary) / 0.8);
    }
  }

  &-destructive {
    --bg-opacity: 1;
    --text-opacity: 1;
    border-color: transparent;
    background-color: hsl(var(--destructive) / var(--bg-opacity));
    color: hsl(var(--destructive-foreground) / var(--text-opacity));

    &:hover {
      background-color: hsl(var(--destructive) / 0.8);
    }
  }

  &-outline {
    background-color: transparent;
    border-color: hsl(var(--border));
    color: hsl(var(--foreground));

    &:hover {
      background-color: transparent;
    }
  }
}

import type { Meta, StoryObj } from "@storybook/react"

import { Badge, badgeVariants } from "."

/**
 * ## Installation
 *
 * ```sh
 * $ yarn add @shadcn-ui-sass/badge
 * # or
 * $ npm install @shadcn-ui-sass/badge
 * ```
 *
 */

const meta: Meta<typeof Badge> = {
  title: "Components/Badge",
  component: Badge,
  tags: ["autodocs"],
}

type Story = StoryObj<typeof Badge>

const template = (args: any) => <Badge {...args}>Badge</Badge>

export const Default: Story = {
  render: (args) => template(args),
}

export const Link: Story = {
  render: (args) => (
    <div className={badgeVariants({ variant: "outline" })} {...args}>
      <a href="!#">Badge</a>
    </div>
  ),
}

export const Secondary: Story = {
  args: {
    variant: "secondary",
  },
  render: (args) => template(args),
}

export const Outline: Story = {
  args: {
    variant: "outline",
  },
  render: (args) => template(args),
}

export const Destructive: Story = {
  args: {
    variant: "destructive",
  },
  render: (args) => template(args),
}

export default meta
