files.content:
.checkbox {
  height: 1rem;
  width: 1rem;
  flex-shrink: 0;
  border-radius: var(--border-radius-soft);
  border: var(--border-size-s) solid hsl(var(--primary));

  &:focus-visible {
    outline: 2px solid transparent;
    outline-offset: 2px;
    box-shadow: 0 0 #0000;
  }

  &:disabled {
    cursor: not-allowed;
    opacity: 0.5;
  }

  &[data-state="checked"] {
    background-color: hsl(var(--primary));
    color: hsl(var(--primary-foreground));
  }

  &-indicator {
    display: flex;
    align-items: center;
    justify-content: center;
    color: currentColor;

    .icon {
      font-size: var(--font-size-base);
    }

    svg {
      width: var(--font-size-base);
      height: var(--font-size-base);
    }
  }

  &:disabled + * {
    cursor: not-allowed;
    opacity: 0.7;
  }
}
