"use client"

import * as React from "react"
import { type DialogProps } from "@radix-ui/react-dialog"
import { Command as CommandPrimitive } from "cmdk"
import { Search } from "lucide-react"

import { cn } from "@/lib/utils"

import { Dialog, DialogContent } from "../dialog"
import "./command.scss"

const Command = React.forwardRef<
  React.ElementRef<typeof CommandPrimitive>,
  React.ComponentPropsWithoutRef<typeof CommandPrimitive>
>(({ className, ...props }, ref) => (
  <CommandPrimitive ref={ref} className={cn("command", className)} {...props} />
))
Command.displayName = CommandPrimitive.displayName

interface CommandDialogProps extends DialogProps {}

const CommandDialog = ({ children, ...props }: CommandDialogProps) => {
  return (
    <Dialog {...props}>
      <DialogContent className="command-content-dialog">
        <Command>{children}</Command>
      </DialogContent>
    </Dialog>
  )
}

const CommandInput = React.forwardRef<
  React.ElementRef<typeof CommandPrimitive.Input>,
  React.ComponentPropsWithoutRef<typeof CommandPrimitive.Input>
>(({ className, ...props }, ref) => (
  <div className="command-input" cmdk-input-wrapper="">
    <Search className="command-input-icon" />
    <CommandPrimitive.Input
      ref={ref}
      className={cn("command-input-input", className)}
      {...props}
    />
  </div>
))

CommandInput.displayName = CommandPrimitive.Input.displayName

const CommandList = React.forwardRef<
  React.ElementRef<typeof CommandPrimitive.List>,
  React.ComponentPropsWithoutRef<typeof CommandPrimitive.List>
>(({ className, ...props }, ref) => (
  <CommandPrimitive.List
    ref={ref}
    className={cn("command-list", className)}
    {...props}
  />
))

CommandList.displayName = CommandPrimitive.List.displayName

const CommandEmpty = React.forwardRef<
  React.ElementRef<typeof CommandPrimitive.Empty>,
  React.ComponentPropsWithoutRef<typeof CommandPrimitive.Empty>
>((props, ref) => (
  <CommandPrimitive.Empty ref={ref} className="command-empty" {...props} />
))

CommandEmpty.displayName = CommandPrimitive.Empty.displayName

const CommandGroup = React.forwardRef<
  React.ElementRef<typeof CommandPrimitive.Group>,
  React.ComponentPropsWithoutRef<typeof CommandPrimitive.Group>
>(({ className, ...props }, ref) => (
  <CommandPrimitive.Group
    ref={ref}
    className={cn("command-group", className)}
    {...props}
  />
))

CommandGroup.displayName = CommandPrimitive.Group.displayName

const CommandSeparator = React.forwardRef<
  React.ElementRef<typeof CommandPrimitive.Separator>,
  React.ComponentPropsWithoutRef<typeof CommandPrimitive.Separator>
>(({ className, ...props }, ref) => (
  <CommandPrimitive.Separator
    ref={ref}
    className={cn("command-separator", className)}
    {...props}
  />
))
CommandSeparator.displayName = CommandPrimitive.Separator.displayName

const CommandItem = React.forwardRef<
  React.ElementRef<typeof CommandPrimitive.Item>,
  React.ComponentPropsWithoutRef<typeof CommandPrimitive.Item>
>(({ className, ...props }, ref) => (
  <CommandPrimitive.Item
    ref={ref}
    className={cn("command-item", className)}
    {...props}
  />
))

CommandItem.displayName = CommandPrimitive.Item.displayName

const CommandShortcut = ({
  className,
  ...props
}: React.HTMLAttributes<HTMLSpanElement>) => {
  return <span className={cn("command-shortcut", className)} {...props} />
}
CommandShortcut.displayName = "CommandShortcut"

export {
  Command,
  CommandDialog,
  CommandInput,
  CommandList,
  CommandEmpty,
  CommandGroup,
  CommandItem,
  CommandShortcut,
  CommandSeparator,
}

.command {
  display: flex;
  height: 100%;
  width: 100%;
  flex-direction: column;
  overflow: hidden;
  border-radius: var(--border-radius-soft);
  background-color: hsl(var(--popover));
  color: hsl(var(--popover-foreground));

  &-content-dialog.dialog-content {
    overflow: hidden;
    padding: var(--space-none);
    box-shadow: var(--shadow-l);

    .command {
      [cmdk-group]:not(hidden) ~ [cmdk-group] {
        padding-top: var(--space-none);
      }

      [cmdk-group] {
        padding-left: var(--space-s);
        padding-right: var(--space-s);
      }

      [cmdk-input-wrapper] svg {
        height: var(--font-size-l);
        width: var(--font-size-l);
      }

      [cmdk-input] {
        height: 3rem;
      }

      [cmdk-item] {
        padding: var(--space-base) var(--space-s);

        .icon {
          font-size: var(--font-size-l);
        }

        svg {
          height: var(--font-size-l);
          width: var(--font-size-l);
        }
      }
    }
  }

  &-input {
    display: flex;
    align-items: center;
    border-bottom: var(--border-size-s) solid hsl(var(--border));
    padding: var(--space-none) var(--space-s);

    &-icon {
      margin-right: var(--space-s);
      flex-shrink: 0;
      opacity: 0.5;
      height: var(--font-size-base);
      width: var(--font-size-base);
    }

    &-input {
      display: flex;
      height: 2.75rem;
      width: 100%;
      border-radius: var(--border-radius-soft);
      background-color: transparent;
      padding: var(--space-none) var(--space-s);
      font-size: var(--font-size-s);
      outline: 2px solid transparent;
      outline-offset: 2px;

      &::placeholder {
        color: hsl(var(--muted-foreground));
      }

      &:disabled {
        cursor: not-allowed;
        opacity: 0.5;
      }
    }
  }

  &-list {
    max-height: 300px;
    overflow-y: auto;
    overflow-x: hidden;
  }

  &-empty {
    padding: var(--space-m) var(--space-none);
    text-align: center;
    font-size: var(--font-size-s);
  }

  &-group {
    overflow: hidden;
    padding: var(--space-xs);
    color: hsl(var(--foreground));

    [cmdk-group-heading] {
      padding: var(--space-xs) var(--space-s);
      font-size: var(--font-size-xs);
      font-weight: var(--font-medium);
      color: hsl(var(--muted-foreground));
    }
  }

  &-separator {
    margin: var(--space-none) calc(var(--space-xs) * -1);
    height: 1px;
    background-color: hsl(var(--border));
  }

  &-item {
    position: relative;
    display: flex;
    cursor: default;
    user-select: none;
    align-items: center;
    border-radius: var(--border-radius-soft);
    padding: var(--space-xs) var(--space-s);
    font-size: var(--font-size-s);
    outline: 2px solid transparent;
    outline-offset: 2px;

    &[aria-selected="true"] {
      background-color: hsl(var(--accent));
      color: hsl(var(--accent-foreground));
    }

    &[data-disabled="true"] {
      pointer-events: none;
      opacity: 0.5;
    }
  }

  &-shortcut {
    margin-left: auto;
    font-size: var(--font-size-xs);
    letter-spacing: 0.1em;
    color: hsl(var(--muted-foreground));
  }
}
