files.content:
.slider {
  align-items: center;
  display: flex;
  position: relative;
  touch-action: none;
  user-select: none;
  width: 100%;

  &-track {
    background-color: hsl(var(--secondary));
    border-radius: var(--border-radius-rounded);
    flex-grow: 1;
    height: var(--space-s);
    overflow: hidden;
    position: relative;
    width: 100%;
  }

  &-range {
    background-color: hsl(var(--primary));
    height: 100%;
    position: absolute;
  }

  &-thumb {
    background-color: hsl(var(--background));
    border: 2px solid hsl(var(--primary));
    border-radius: var(--border-radius-rounded);
    display: block;
    height: 20px;
    transition: 0.3s ease;
    width: 20px;

    &:focus-visible {
      outline: 2px solid hsl(var(--ring));
      outline-offset: 2px;
    }

    &[disabled] {
      opacity: 0.5;
      pointer-events: none;
    }
  }
}
