import * as React from "react"

import { cn } from "@/lib/utils"

import "./textarea.scss"

export interface TextareaProps
  extends React.TextareaHTMLAttributes<HTMLTextAreaElement> {}

const Textarea = React.forwardRef<HTMLTextAreaElement, TextareaProps>(
  ({ className, ...props }, ref) => {
    return (
      <textarea className={cn("text-area", className)} ref={ref} {...props} />
    )
  }
)
Textarea.displayName = "Textarea"

export { Textarea }

/*
placeholder:text-muted-foreground */

.text-area {
  background-color: hsl(var(--background-color));
  border: var(--border-size-s) solid hsl(var(--input));
  border-radius: var(--border-radius-soft);
  display: flex;
  font-size: var(--font-size-s);
  min-height: 80px;
  padding: var(--space-s) var(--space-base);
  width: 100%;

  &::placeholder {
    color: hsl(var(--muted-foreground));
  }

  &:focus {
    outline: 1px solid hsl(var(--ring));
  }

  &:focus-visible {
    outline: 2px solid hsl(var(--ring));
    outline-offset: 2px;
  }

  &[disabled] {
    cursor: not-allowed;
    opacity: 0.5;
  }
}

import type { Meta, StoryObj } from "@storybook/react"

import { Textarea } from "."

/**
 * ## Installation
 *
 * ```sh
 * $ yarn add @shadcn-ui-sass/textarea
 * # or
 * $ npm install @shadcn-ui-sass/textarea
 * ```
 *
 */

const meta: Meta<typeof Textarea> = {
  title: "Components/Textarea",
  component: Textarea,
  tags: ["autodocs"],
  args: {
    placeholder: "placeholder",
    disabled: false,
  },
}

type Story = StoryObj<typeof Textarea>

const template = (args: any) => <Textarea {...args} />

export const Default: Story = {
  render: (args) => template(args),
}

export const Disabled: Story = {
  args: {
    disabled: true,
  },
  render: (args) => template(args),
}

export default meta
